import { useEffect, useState } from "react";
import "./ToDo.scss";
import axios from "axios";

interface ToDo {
  title: string;
  description: string;
}

const ToDo: React.FC = () => {
  const [toDo, setToDo] = useState<ToDo>({ title: "", description: "" });
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<any>(null);
  const [toDos, setToDos] = useState<ToDo[]>([]);
  const [refresh, setRefresh] = useState<boolean>(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setToDo({ ...toDo, [e.target.name]: e.target.value });
    setError("");
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (toDo.title.trim() === "" || toDo.description.trim() === "") {
      setError("Fields can't be empty");
      return;
    }
    setIsLoading(true);
    try {
      const newToDo: ToDo = toDo;
      const response = await axios.post("http://localhost:8080/todos", newToDo);
      setToDo({ title: "", description: "" });
      console.log(response.data);
      setRefresh(true);
    } catch (error) {
      setError((error as Error).message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    const fetchToDos = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get("http://localhost:8080/todos");
        setToDos(response.data as ToDo[]);
      } catch (error) {
        setError((error as Error).message);
      } finally {
        setIsLoading(false);
        setRefresh(false);
      }
    };
    fetchToDos();
  }, [refresh]);

  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error occurred: {error}</p>;
  }

  return (
    <div>
      <div className="mb-3">
        <form onSubmit={handleSubmit}>
          <div className="container pt-3 pb-3">
            <div className="mb-3">
              <label>Title: </label>
              <input
                type="text"
                placeholder="enter title"
                className="input-title"
                onChange={handleChange}
                name="title"
              />
            </div>

            <div className="mb-3">
              <label>Description: </label>
              <input
                type="text"
                placeholder="enter description"
                className="input-desc"
                onChange={handleChange}
                name="description"
              />
            </div>

            <div className="mb-3">
              <button className="btn btn-primary">
                {isLoading ? "POSTING" : "POST"}
              </button>
            </div>

            <div>
              <p>{error}</p>
            </div>
          </div>
        </form>
      </div>

      <div>
        <div className="container">
          {toDos.map((eachToDo, index) => (
            <div key={index}>
              <p>{eachToDo.title}</p>
              <p>{eachToDo.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ToDo;
